.<template>
  <div>
    <div class="row justify-content-center">
      <div class="row my-5 col-12">
        <div
          class="card-body border col-3 m-3"
          v-for="(item, index) in studentData"
          :key="index"
        >
          <h5 class="card-title">
            {{ item.nama + ", " + item.umur + " Years Old" }}
          </h5>
          <p class="card-text">{{ item.deskripsi_diri }}</p>
          <p class="card-text">Email : {{ item.email }}</p>
          <p class="card-text">Soft Skill : {{ item.soft_skill }}</p>
          <p class="card-text">Hard Skill : {{ item.hard_skill }}</p>
          <p class="card-text">Interest : {{ item.interest }}</p>

          <div class="d-flex justify-content-between">
            <div>
              <router-link to="/update">
                <button class="btn btn-primary mr-2">Update</button>
              </router-link>
              <router-link to="/"
                ><button
                  class="btn btn-danger"
                  @click="deleteStudentFunc(item.id)"
                >
                  Delete
                </button></router-link
              >
            </div>
            <div class="d-flex justify-content-end">
              <img
                class="w-25"
                v-if="item.jenis_kelamin == 'Male'"
                src="../assets/male.png"
                alt="male"
              />
              <img
                class="w-25 justify-content-end"
                v-if="item.jenis_kelamin == 'Female'"
                src="../assets/female.png"
                alt="female"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import studentService from "../services/studentService";

export default {
  name: "ContentComp",
  data() {
    return {
      studentData: null,
    };
  },

  components: {},

  methods: {
    getStudent() {
      studentService
        .getAll()
        .then((response) => {
          this.studentData = response.data;
          console.log(this.studentData);
        })
        .catch((e) => {
          console.log(e);
        });
    },
  },

  mounted() {
    this.getStudent();
  },
};
</script>

