.<template>
  <div>
    <div class="d-flex justify-content-center">
      <div class="row my-5 col-12">
        <div
          class="card-body border col-4 m-3"
          v-for="(item, index) in studentData"
          :key="index"
        >
          <h5 class="card-title">
            {{ item.nama + ", " + item.umur + " Years Old" }}
          </h5>
          <p class="card-text">{{ item.deskripsi_diri }}</p>
          <p class="card-text">Email : {{ item.email }}</p>
          <p class="card-text">Soft Skill : {{ item.soft_skill }}</p>
          <p class="card-text">Hard Skill : {{ item.hard_skill }}</p>
          <p class="card-text">Interest : {{ item.interest }}</p>

          <div class="d-flex flex-row justify-content-between">
            <div>
              <router-link :to="{ path: '/update/' + item.id }">
                <button class="btn btn-primary">Update</button>
              </router-link>

              <router-link to="/"
                ><button
                  class="btn btn-danger ml-3"
                  @click="deleteStudent(item.id, index)"
                >
                  Delete
                </button></router-link
              >
            </div>
            <div>
              <img
                class="male"
                v-if="item.jenis_kelamin == 'Male'"
                src="../assets/male.png"
                alt="male"
              />
              <img
                class="male"
                v-if="item.jenis_kelamin == 'Female'"
                src="../assets/female.png"
                alt="female"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import studentService from "../services/studentService";

export default {
  name: "ContentComp",
  data() {
    return {
      studentData: null,
    };
  },

  components: {},

  methods: {
    getStudent() {
      studentService
        .getAll()
        .then((response) => {
          this.studentData = response.data;
          console.log(this.studentData);
        })
        .catch((e) => {
          console.log(e);
        });
    },
  },

  deleteStudent(id, index) {
    studentService
      .delete(id)
      .then((response) => {
        console.log(response.data);
        this.studentData.splice(index, 1);
      })
      .catch((e) => {
        console.log(e);
      });
  },

  mounted() {
    this.getStudent();
  },
};
</script>

<style scoped>
.male {
  width: 40px;
}
</style>
